mapper.select:
SELECT
FROM weixin_msg a INNER JOIN (SELECT MAX(id) id FROM weixin_msg WHERE official = 0 GROUP BY openid) t ON a.id = t.id
where 1 = 1
and content like CONCAT('%', upper(#{msg.content}), '%')
and a.openid = #{msg.openid,jdbcType=VARCHAR}
and collect = #{msg.collect,jdbcType=INTEGER}
and u.nickname like CONCAT('%', upper(#{msg.userInfo.nickname}), '%')
and u.remark like CONCAT('%', upper(#{msg.userInfo.remark}), '%')
ORDER BY a.id desc
limit #{page.startRow, jdbcType=INTEGER},#{page.endRow, jdbcType=INTEGER}

SELECT
FROM weixin_msg a
where 1 = 1
and content like CONCAT('%', upper(#{msg.content}), '%')
and a.openid = #{msg.openid,jdbcType=VARCHAR}
and collect = #{msg.collect,jdbcType=INTEGER}
and u.nickname like CONCAT('%', upper(#{msg.userInfo.nickname}), '%')
and u.remark like CONCAT('%', upper(#{msg.userInfo.remark}), '%')
ORDER BY a.id desc
limit #{page.startRow, jdbcType=INTEGER},#{page.endRow, jdbcType=INTEGER}

select 

from weixin_msg a

where a.id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE weixin_msg
   SET reply = 1
 WHERE official = 0
   AND create_date <= NOW()
   AND appid = #{appid, jdbcType=VARCHAR}
   AND openid = #{openid, jdbcType=VARCHAR}
