mapper.select:
select

from weixin_tag
where id = #{entity.id,jdbcType=VARCHAR}
and appid = #{entity.appid, jdbcType=VARCHAR}

SELECT
FROM weixin_tag WHERE 1=1
AND appid = #{entity.appid}
AND upper(name) LIKE CONCAT('%', upper(#{entity.name}), '%')
AND id = #{entity.id}
ORDER BY create_date desc

SELECT

FROM weixin_tag
WHERE 1=1

    AND appid  = #{entity.appid}


    AND upper(name)  LIKE
    CONCAT('%', upper(#{entity.name}), '%')


    AND id  = #{entity.id}

ORDER BY create_date desc



        limit  #{page.startRow, jdbcType=INTEGER},#{page.endRow, jdbcType=INTEGER}

SELECT count(*) FROM weixin_tag WHERE 1=1
AND appid = #{entity.appid}
AND upper(name) LIKE CONCAT('%', upper(#{entity.name}), '%')
AND id = #{entity.id}

mapper.delete:
delete from weixin_tag
where id = #{entity.id,jdbcType=INTEGER}
and appid = #{entity.appid, jdbcType=VARCHAR}
